const vcard = $json.vcard;

// split the string on \n or \r characters
const lines = vcard.text.match(/[^\r\n]+/g);

const fn = lines.find(element => element.startsWith("FN:"));
if (fn) {
  vcard.fn = fn.substring(3);
}

const items = [];

function HandlerPhone(item, phoneline){
  // getting phone
    item.phone = phoneline.split(":").pop();
    if (item.phone) {
      item.phone = item.phone.replace(/\D/g, "");
      if (item.phone.length === 0) item.phone = undefined;
    }

    // getting phone old method
    item.phone_old = phoneline.split('TEL')[1]?.split(':')[0]?.split('waid=')[1];
    if (item.phone_old) {
      item.phone_old = item.phone_old.replace(/\D/g, "");
      if (item.phone_old.length === 0) item.phone_old = undefined;
    }

    // getting whatsapp id
    const waidindex = phoneline.indexOf("waid=");
    if (waidindex > -1) {
      item.waid = phoneline.substring(waidindex + 5).split(':')[0];
      if (item.waid) {
        item.waid = item.waid.replace(/\D/g, "");
        if (item.waid.length === 0) item.waid = undefined;
      }
    }    
}

function ForEachItem(element, index, array) {
  element = element.substring(4);
  const itemindex = element.split('.')[0];    
  const telprefix = itemindex + ".TEL;";
  if (element.startsWith(telprefix)) {
    element = element.substring(5);

    let item = items.find(s => s.index == itemindex);
    if (!item) { item = { index: itemindex }; items.push(item); }

    HandlerPhone(item, element);
  }    
}

function ForEachTEL(element, index, array) {
  element = element.substring(4);  

  const item = {}; 
  items.push(item);

  HandlerPhone(item, element);  
}

const itemlines = lines.filter(element => element.startsWith("item"));
itemlines.forEach(ForEachItem);

const tellines = lines.filter(element => element.startsWith("TEL;"));
tellines.forEach(ForEachTEL);

vcard.items = items;

if (vcard.items.length > 0) {
  // setting default phone
  vcard.phone = vcard.items[0].waid ?? vcard.items[0].phone ?? vcard.items[0].phone_old;
}

return $input.item;

